using UnityEngine;public class Girl2D : MonoBehaviour{	[Range(0.2f, 2f)] public float m_MoveDistance = 1f;		private Animator m_Animator;	private Transform m_Trans;	private Vector3 m_InitPos;	private float m_PrevFramePosX;		void Start ()	{		m_Animator = this.GetComponent<Animator> ();		m_Trans = this.GetComponent<Transform> ();		m_InitPos = m_Trans.position;	}	void Update ()	{		float x = m_InitPos.x + Mathf.Sin (Time.time) * m_MoveDistance;		m_Trans.position = new Vector3 (x, m_InitPos.y, m_InitPos.z);				float dtx = x - m_PrevFramePosX;		if (dtx > 0f)			m_Animator.SetInteger ("Dir", 2);		else if (dtx < 0f)			m_Animator.SetInteger ("Dir", 1);				m_PrevFramePosX = m_Trans.position.x;	}}