using UnityEngine;[RequireComponent(typeof(MeshRenderer))]public class Lake2DWater : MonoBehaviour{	public Vector2 m_Speed = new Vector2 (0.01f, 0f);	public Color m_Color = Color.white;	[Range(0.01f, 0.03f)] public float m_Distortion = 0.013f;	Material m_Mat;	void Awake ()	{		Renderer rd = GetComponent<Renderer> ();		rd.sortingOrder = -1;		m_Mat = rd.material;	}	void LateUpdate ()	{		Vector2 scroll = Time.deltaTime * m_Speed;		m_Mat.mainTextureOffset += scroll;		m_Mat.SetColor ("_Color", m_Color);		m_Mat.SetFloat ("_Distortion", m_Distortion);	}}